    }
    vector<vector<int>> spiralMatrixIII(int rows, int cols, int rStart, int cStart) 
    {
        int cnt=1;
        vector<vector<int>> ans;
        vector<int> v={rStart,cStart};
        ans.push_back(v);
        int count=0, d=0, steps=1;
        int r=rStart, c=cStart;
        while(cnt!=(rows*cols))
        {
            for(int x=1; x<=steps; x++)
            {
                if(d==0)
                {
                    c++;
                    if(liesInside(r,c,rows,cols)) 
                    {
                        vector<int> v={r,c};
                        ans.push_back(v);
                        cnt++;
                    }
                }
                else if(d==1)
                {
                    r++;
                    if(liesInside(r,c,rows,cols)) 
                    {
                        vector<int> v={r,c};
                        ans.push_back(v);
                        cnt++;
                    }
                }
                else if(d==2)
                {
                    c--;
                    if(liesInside(r,c,rows,cols)) 
                    {
                        vector<int> v={r,c};
                        ans.push_back(v);
                        cnt++;
