class Solution {
public:
    int removeDuplicates(vector<int>& nums) {
        int c = 0;                             // Initialize a counter 
        int curr = nums[0];                    // store current value at 0th index in a variable
        for (int i = 1; i < nums.size(); ++i) {
            if(nums[i] == curr && c < 1) {
                c++;                           // If a current value repeats itself atmost twice increase counter (i.e. c=1)
            }
            else if(nums[i] == curr && c >= 1) {
                nums.erase(nums.begin()+(i));  // If current value repeats more than twice than erase that value
                i--;                           // To repeat the current index with new value
            }
            else {
                curr = nums[i];                // update curr value
                c = 0;
            }
        }
        return nums.size();
    }
};
