class Solution {
    public int removeDuplicates(int[] nums) {
        if (nums.length <= 2) return nums.length;
        int validPos = 0;
        int currCount = 1;
        for (int i = 1; i < nums.length; i++) {
                currCount = nums[validPos] != nums[i] ? 1 : currCount+1;
                if (currCount <= 2) nums[++validPos] = nums[i];        
        }
        return ++validPos;
    }
}

