class RandomizedSet {
    unordered_map<int, int> mymap;
    vector<int> myvec;

public:
    /** Initialize your data structure here. */
    RandomizedSet() {

    }

    /** Inserts a value to the set. Returns true if the set did not already contain the specified element. */
    bool insert(int val) {
        if (mymap.find(val) != mymap.end()) return false;
        myvec.push_back(val);
        int index = myvec.size() - 1;
        mymap[val] = index;
        return true;
    }

    /** Removes a value from the set. Returns true if the set contained the specified element. */
    bool remove(int val) {
        if (mymap.find(val) == mymap.end()) return false;
        // Reset the last value of myvec to the index and pop back
        int index = mymap[val];
        int last_val = myvec.back();
        myvec[index] = last_val;
        myvec.pop_back();
        mymap[last_val] = index;
        mymap.erase(val);
        return true;
    }

    /** Get a random element from the set. */
    int getRandom() {
        if (myvec.size() == 1) return myvec[0];
        int size  = myvec.size();
        //cout << "size is " << size << endl;
        int index = rand() % (size);
        return myvec[index];

    }
};
```

