class RandomizedSet {
public:
    unordered_set<int> s;
    RandomizedSet() {

    }

    bool insert(int val) {
        if(s.find(val)==s.end())
        {
            s.insert(val);
            return true;
        }
        else
        return false;
    }

    bool remove(int val) {
        if(s.find(val)==s.end())
        return false;
        else
        {
            s.erase(val);
            return true;
        }
    }

    int getRandom() {
        int t = rand()%s.size();
        auto it = s.begin();
        advance(it,t);
        return *(it);
    }
};

