        int index = hash.get(val);
        if(index < list.size() - 1) {
            list.set(index, list.get(list.size() -1));
        }
        hash.put(list.get(list.size() - 1), index);
        hash.remove(val);
        list.remove(list.size() - 1);
        return true;
    }

