class RandomizedSet { // An elegant WRONG Solution for fun in the name of study
public:
    /** Initialize your data structure here. */
    RandomizedSet() {       }

    /** Inserts a value to the set. Returns true if the set did not already contain the specified element. */
    bool insert(int val) {
        return m_.insert(val).second;       
    }

    /** Removes a value from the set. Returns true if the set contained the specified element. */
    bool remove(int val) {        
        return m_.erase(val); // return the number of elements erased.                       
    }

    /** Get a random element from the set. */
    int getRandom() {
        if(m_.size() < 1 ) return -1;
        int index = rand() % m_.size();        
        //return *(m_.begin() + index); // Note: Err. No + operation for for C++ hashset/hashmap
        auto it=m_.begin();
        //while(index-- > 0) it++; // ok
        std::advance(it, index);
        return *it;
    }

private:      
    unordered_set<int> m_;  
};
