void randomizedSetNodeFree(RandomizedSetNode* obj) {
    if (obj->apstSucc[0] != NULL) {
        randomizedSetNodeFree(obj->apstSucc[0]);
    }
    if (obj->apstSucc[1] != NULL) {
        randomizedSetNodeFree(obj->apstSucc[1]);
    }
