    bool insert(int val) {
       if(m.count(val))
       return false;
       m[val]=v.size();
       v.push_back(val);
       return true;
    }

    bool remove(int val) {
      if(!m.count(val))
      return false;
      int p=v.back();
      m[p]=m[val];
      v[m[val]]=p;
      v.pop_back();
      m.erase(val);
      return true;
    }

    int getRandom() {
     return v[rand()% v.size()];
    }
};

