        int res = Collections.binarySearch(list, val);
        if (res < 0) {
            list.add(-res - 1, val);
            return true;
        } else return false;
    }

    public boolean remove(int val) {
        int res = Collections.binarySearch(list, val);
        if (res < 0) {
            return false;
        } else {
            list.remove(res);
            return true;
        }
    }

    public int getRandom() {
        return list.get(random.nextInt(list.size()));
    }
}
