        for(int i=0;i<nums.length-k;i++){
            int left=nums[i];  // number to be removed from the window
            int right=nums[i+k];  // number to be added to the window
            int index=Collections.binarySearch(list,right);
            if(index>=0)  // if already present insert at the already existing position
				list.add(index,right);
            else
				list.add(-index-1,right);
			/* if element is not present binarySearch returns
			-(index)-1 which is the index at which it should be inserted to
			be in the correct order.*/ 
            index=Collections.binarySearch(list,left);
            list.remove(index);
            res[i+1]=(k%2==0)?((double)list.get(k/2-1)+(double)list.get(k/2))/2:list.get(k/2);
        }
        return res;
