class Solution {
public:
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
    multiset<long> s; // create a multiset to store the elements of the window
    vector<double> v; // create a vector to store the median values
        // insert the first k-1 elements into the multiset
        for(int i=0;i<k-1;i++)
            s.insert(nums[i]);

        // initialize the variable for the index of the element to be inserted
        int j=k-1;

        // loop through the rest of the elements
        while(j<nums.size()) {
            s.insert(nums[j]); // insert the current element into the multiset
            auto it=s.begin(); // initialize an iterator to the beginning of the multiset
            long long x; // variable to store the value of the median if k is even

            // if k is even, advance the iterator to the (k/2)-1th element and store it in x
            if(k/2>=1) {
                advance(it,k/2-1);
                x=*it;
                advance(it,1); // advance the iterator one more time to point to the median
            }
            // if k is odd, the median is the value pointed to by the iterator
            if(k%2!=0)
                v.push_back(double(*it)/double(1));
            // if k is even, the median is the average of the values pointed to by the iterator and x
            else
                v.push_back(double(*it+x)/double(2));

            s.erase(s.lower_bound(nums[j-k+1])); // remove the element at the beginning of the window from the multiset
            j++; // increment the index of the element to be inserted
        }
        return v; // return the vector of median values
    }
};
