#include <ext/pb_ds/assoc_container.hpp> 
using namespace __gnu_pbds; 
typedef tree<int, null_type, 
             less_equal<int>, rb_tree_tag, 
             tree_order_statistics_node_update> 
    ordered_set; 
class Solution {
public:
    vector<double> medianSlidingWindow(vector<int>& arr, int k) {
        int n=arr.size();
        vector<double>res;
        ordered_set os;
        for(int i=0;i<k;i++){
            os.insert(arr[i]);
        }
        if(k&1)
        {
            int ans=*os.find_by_order(k/2);
            res.push_back((double)(ans));

            for(int i=0;i<n-k;i++){
                os.erase(os.find_by_order(os.order_of_key(arr[i])));
                os.insert(arr[i+k]);
                ans=*os.find_by_order(k/2);
                res.push_back((double)(ans));
            }
        }
        else
        {
            double l=*os.find_by_order(k/2-1);
            double r=*os.find_by_order(k/2);
            double ans=(l+r)/2.0;
            res.push_back(ans);
            for(int i=0;i<n-k;i++){
                os.erase(os.find_by_order(os.order_of_key(arr[i])));
                os.insert(arr[i+k]);
                l=*os.find_by_order(k/2-1);
                r=*os.find_by_order(k/2);
                ans=(l+r)/2.0;
                res.push_back(ans);
            }
        }
        return res;
    }
};
