    vector<double> medianSlidingWindow(vector<int>& a, int k) {
        ordered_set os;
        int i,j,n=a.size();
        for(int i=0;i<k;i++){
            os.insert({a[i],i});
        }
        vector<double> v;
        k--;
        int t1=k/2,t2=k+1;
        t2/=2;
        k++;
        for(int i=k;i<=n;i++){
            long long int o=os.find_by_order(t1)->first;
            long long int t=os.find_by_order(t2)->first;
            if(k%2)
            v.push_back(o);
            else{
                o+=t;
                v.push_back(o/2.0);
            }
            os.erase({a[i-k],i-k});
            if(i<n)
            os.insert({a[i],i});
        }
        return v;
    }
};
