from sortedcontainers import SortedList

class Solution:
    def medianSlidingWindow(self, nums: List[int], k: int) -> List[float]:
        ans = []
        sl = SortedList()
        for i, x in enumerate(nums): 
            sl.add(x)
            if i >= k: sl.remove(nums[i-k])
            if i+1 >= k: 
                val = sl[k//2]
                if not k&1: val = (sl[(k-1)//2] + val)/2
                ans.append(val)
        return ans 
