class Solution {
public:
    void Balance(multiset<int> & left, multiset<int> & right) {
        int n = left.size();
        int m = right.size();
        if (n == m || n+1 == m) return;
        if (n > m) {
            auto it = left.end();
            it--;
            right.emplace(*it);
            left.erase(it);
        } 
        else {
            left.emplace(* right.begin());
            right.erase(right.begin());
        }
    }
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        vector<double> ans;
        multiset<int> left;
        multiset<int> right;

        for(int i = 0; i < nums.size(); i++) {
            if (!right.empty() && nums[i] < * right.begin()) left.insert(nums[i]);
            else right.insert(nums[i]);
            Balance(left, right);
            if (i >= k) {
                auto it = left.find(nums[i-k]);
                if (it != left.end()) left.erase(it);
                else it = right.find(nums[i-k]), right.erase(it);
                Balance(left, right);
            }
          if (i >= k-1) { 
              double res = 0;
              auto it = right.begin();
              if (k % 2 == 0) { 
                  auto it_ = left.end();
                  it_--;
                  res = (double)( (long long) *it + *it_ ) / 2;
              }
              else res = *it;
              ans.push_back(res);
          }
        }
        return ans;
    }
};

