class Solution {
public:
    int getIndex(vector<int>& window,int element){ //binary search
        int first = 0;
        int last = window.size()-1;
        while(first<=last){
            int mid = (first+last)/2;
            if(window[mid] == element)
                return mid;
            if(window[mid] > element)
            {
                last = mid - 1;
            }
            else
            {
                first = mid + 1;
            }
        }
        return first; //if not found return position where element needs to be inserted
    }
    void eraseAndAdd(vector<int>& slidingWindow,int deleteElement,int addElement){
        int size = slidingWindow.size();
        int deleteIndex = getIndex(slidingWindow,deleteElement);
        slidingWindow.erase(slidingWindow.begin()+deleteIndex);
        int addIndex = getIndex(slidingWindow,addElement);
        slidingWindow.insert(slidingWindow.begin()+addIndex,addElement);
        // cout<<" delete index "<<deleteElement<<" is "<<deleteIndex<<" insert index "<<addElement<<" is"<<addIndex<<endl; 
    }
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        vector<int> slidingWindow;
        vector<double> result;
        for(int i = 0;i<k;i++)
        {
            slidingWindow.push_back(nums[i]);  //initial window creation
        }
        sort(slidingWindow.begin(),slidingWindow.end()); //sorting initial window
        bool even=false;
        if(k%2 == 1){
            result.push_back(slidingWindow[(k/2)]);
        }
        else{
            result.push_back(slidingWindow[(k/2)]/2.0 + slidingWindow[(k/2)-1]/2.0);
            even=true;
        }
        int size = nums.size();
        for(int i = 1;i<size-k+1;i++){
            eraseAndAdd(slidingWindow,nums[i-1],nums[i+k-1]); //adding next element to window & deleting element before start of window
            if(even)
            {
                result.push_back(slidingWindow[(k/2)]/2.0 + slidingWindow[(k/2)-1]/2.0);
            }
            else
            {
                result.push_back(slidingWindow[(k/2)]);
            }    
        }
        return result;
    }
};
