class Solution {

    public double[] medianSlidingWindow(int[] nums, int k) {

        // maxHeap maitians the half small part
        // maitain the propetry (*): maxHeap.size() - minHeap.size() = 0 or 1 
        if(nums.length==0 || k<=0){
            return new double[nums.length];
        }
        int mincap = k/2, maxcap = k-k/2, n = nums.length;
        HashHeap<Integer> maxHeap = new HashHeap<>(Collections.reverseOrder());
        HashHeap<Integer> minHeap = new HashHeap<>();
        for(int i=0; i<k; i++){
            maxHeap.offer(nums[i]);
        }
        for(int i=0; i<mincap; i++){
            minHeap.offer(maxHeap.poll());
        }
        double[] ans = new double[n-k+1];
        ans[0] = getMedian(maxHeap, minHeap, k);
        for(int i=k; i<n; i++){

            // remove: maxHeap.size()-minHeap.size() falls in the range [-1, 0];
            if(maxHeap.contains(nums[i-k])){
                maxHeap.remove(nums[i-k]);
            }
            else{
                minHeap.remove(nums[i-k]);
            }

            // always add new element into the maxHeap.
            maxHeap.offer(nums[i]);

            // Claim: maxHeap.size()-minHeap.size() falls in the range [0, 3]
            // rebalance only if maxHeap.size90 - minHeap.size() = 2 or 3 in order to maintain the property *.
            if(maxHeap.size()-minHeap.size()>1){
                minHeap.offer(maxHeap.poll());
            }

            if(minHeap.size()>0 && maxHeap.peek()>minHeap.peek()){
                int tmp1 = maxHeap.poll();
                int tmp2 = minHeap.poll();
                maxHeap.offer(tmp2);
                minHeap.offer(tmp1);
            }

            ans[i-k+1] = getMedian(maxHeap, minHeap, k);
        }

        return ans;
    }

    private double getMedian(HashHeap<Integer> maxHeap, HashHeap<Integer> minHeap, int k){
        double left = maxHeap.peek();
        if((k&1)==1){
            return left;
        }
        return (left + minHeap.peek())/2;
    }

    private class HashHeap<T>{

        // all method take logarithmic time
        TreeMap<T, Integer> mp;
        int size;
        HashHeap(){
            mp = new TreeMap<>();
        }
        HashHeap(Comparator<T> comparator){
            mp = new TreeMap<>(comparator);
        }

        int size(){
            return size;
        }

        T peek(){
            if(size==0){
                return null;
            }

            return mp.firstEntry().getKey();
        }

        T poll(){
            if(size==0){
                return null;
            }
            size--;
            Map.Entry<T, Integer> top= mp.firstEntry();
            if(top.getValue()==1){
                mp.remove(top.getKey());
            }
            else{
                mp.put(top.getKey(), top.getValue()-1);
            }
            return top.getKey();
        }

        void offer(T item){
            size++;
            mp.put(item, mp.getOrDefault(item, 0)+1);
        }

        boolean contains(T item){
            return mp.containsKey(item);
        }

        boolean remove(T item){
            Integer freq = mp.get(item);
            if(freq==null){
                return false;
            }
            size--;
            int f = freq;
            if(f==1){
                mp.remove(item);
            }
            else{
                mp.put(item, f-1);
            }
            return true;
        }
    }


}
