#include <bits/stdc++.h>
#include <ext/pb_ds/assoc_container.hpp>
#include <ext/pb_ds/tree_policy.hpp>
using namespace __gnu_pbds;
using namespace std;
using ll = long long;
#define ordered_set  tree< int ,null_type,less<int>,rb_tree_tag,tree_order_statistics_node_update> // for tree implementation with less value.
#define ordered_multisetset  tree< ll ,null_type,less_equal<ll>,rb_tree_tag,tree_order_statistics_node_update> // for tree implementation with less or equal to value.#includ<bits/stdc++.h>

class Solution {
public:
    ordered_multisetset s;
    vector<double> ans;

    void add(){
        int n=s.size();
        if(n%2!=0){
            ans.push_back(*s.find_by_order(n/2));
        }
        else{
            ans.push_back((*s.find_by_order(n/2) + *s.find_by_order(n/2-1))/2.0);
        }
    }

    vector<double> medianSlidingWindow(vector<int>& a, int k) {

        for( int i=0;i<k;i++) s.insert(a[i]);
        add();
        int i=0,j=k;

        for( ;j<a.size();j++,i++){

            ll in=s.order_of_key(a[i]);
            s.erase(s.find_by_order(in));

            s.insert(a[j]);
            add();
        }
        return ans;
    }
};
