class Solution {
public:

    #define pb push_back
    #define ff first
    #define ss second


    //initialising...
    priority_queue<int>pq;//max_heap
    priority_queue<int,vector<int>,greater<int>>pq2;//min heap
    vector<double>ans;

    //balancing
    void balance(){
        if(pq.size()>(pq2.size()+1)){
            pq2.push(pq.top());
            pq.pop();
        }
        if(pq2.size()>(pq.size()+1)){
            pq.push(pq2.top());
            pq2.pop();
        }
    }

    //adding new val
    void add(int val){
        if((pq.empty()&&pq2.empty())||val<pq.top()){
            pq.push(val);
        }else pq2.push(val);
    }

    //adding median to our answer!
    void updateAns(){
        if(pq.size()==pq2.size()){
            double one=pq.top();
            one+=pq2.top();
            ans.pb(one/2);
        }
        else ans.pb((pq.size()>pq2.size())?pq.top():pq2.top());
    }

    vector<double> medianSlidingWindow(vector<int>& a, int k) {
        int n=a.size();
        for(int i=0;i<k;i++){
            add(a[i]);
            balance();
        }
        updateAns();
        for(int i=k;i<n;i++){
            //removing prev val
            int prev=a[i-k],indx=0;
            vector<int>temp;
            if(!pq.empty()&&prev<=pq.top()){
                while(prev!=pq.top()){
                    temp.pb(pq.top());
                    pq.pop();
                }
                pq.pop();
                int l=temp.size();
                while(indx<l){pq.push(temp[indx++]);}
