                while(prev!=(pq2.top())){
                    temp.pb(pq2.top());
                    pq2.pop();
                }
                pq2.pop();
                int l=temp.size();
                while(indx<l){pq2.push(temp[indx++]);}
            }
            add(a[i]);
            balance();
            updateAns();
        }
        return ans;
    }
};
