class Solution {
public:
// calculates the median for given left and right
    double calc(multiset<int> &left,multiset<int> &right,int k){

        if(k%2){
            return *right.begin();
        }else{
            return ((double)((*left.rbegin())*1LL + (*right.begin())))/2;
        }
    }
	// balance function balances the multiset such that it first floor(k/2) elements are in left multiset and last ceil(k/2) elements are in right multiset
    void balance(multiset<int> &left,multiset<int> &right,int k){
        if(k<=2)return ;
         while(left.size() < k/2){
            int curr = *right.begin();
            right.erase(right.begin());
            left.insert(curr);
        }
        while(*left.rbegin() > *right.begin()){
            int a = *left.rbegin();
            int b = *right.begin();
            auto aErase = left.find(a);
            left.erase(aErase);
            right.erase(right.begin());
            right.insert(a);
            left.insert(b);
        }
    }
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        int n = nums.size();
        vector<double> ans;
        multiset<int> left;
        multiset<int> right;
        for(int i = 0;i<k;i++){
            right.insert(nums[i]);
        }
       if(k<=2){
           for(int i = 0;i<=n-k;i++){
                int end = i + k - 1;
                double temp = ((double)(nums[i]*1LL + nums[end]))/2;
                ans.push_back(temp);

            }
           return ans;
       }

        balance(left,right,k);
        ans.push_back(calc(left,right,k));
        for(int i = 1;i<=n-k;i++){
            // sliding the window erasing the element that went out of the window and inserting the element that came in the window 
            int end = i + k - 1;
            if(k <= 2){
                double temp = (nums[i] + nums[end])/2;
                ans.push_back(temp);
                continue;
            }
            int toDelete = nums[i-1];
            //cout<<"toDelete "<<toDelete<<endl;
            auto findInLeft = left.find(toDelete);
            if(findInLeft != left.end()){
                left.erase(findInLeft);
            }else{
                auto findInRight = right.find(toDelete);
                right.erase(findInRight);
            }
            int toInsert = nums[end];
            right.insert(toInsert);
            balance(left,right,k);
            ans.push_back(calc(left,right,k));   
        }
        return ans;
    }
};
