type maxH []int

func (h *maxH) remove(item int) {
	for i := 0; i < len(*h); i++ {
		if item == (*h)[i] {
			(*h)[i] = (*h)[len(*h)-1]
			*h = (*h)[:len(*h)-1]

			if i == 0 {
				h.heapDown(0)
				return
			}

			parent := (i - 1) / 2

			if parent >= 0 && i < len(*h) {
				h.heapDown(i)
				h.heapUp(i)
				return
			}
		}
	}
}

