func (h *minH) heapDown(p int) {
	l, r := 2*p+1, 2*p+2
	smaller := p

	if l < len(*h) && (*h)[l] < (*h)[smaller] {
		smaller = l
	}

	if r < len(*h) && (*h)[r] < (*h)[smaller] {
		smaller = r
	}

