```
class Solution {
public:
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        vector<double> ans;
        int n=nums.size();
        priority_queue<int,vector<int>, greater<int> > min_half;
        priority_queue<int> max_half;
        for(int i=0;i<k;i++) max_half.push(nums[i]);
        for(int i=0;i<k/2;i++){
            min_half.push(max_half.top());
            max_half.pop();
        }
        if(k&1) ans.push_back(max_half.top());
        else ans.push_back((1LL*min_half.top()+1LL*max_half.top())*1.0/2);
        multiset<int> del_max,del_min;
        for(int i=k;i<n;i++){
            if(nums[i-k]<=max_half.top()){ //target->remove from max_half && add to max half
                del_max.insert(nums[i-k]);
                min_half.push(nums[i]);
                while(del_min.find(min_half.top())!=del_min.end()){
                    del_min.erase(del_min.find(min_half.top()));
                    min_half.pop();
                }
                max_half.push(min_half.top());
                min_half.pop();
            }
            else{
                del_min.insert(nums[i-k]);
                max_half.push(nums[i]);
                while(del_max.find(max_half.top())!=del_max.end()){
                    del_max.erase(del_max.find(max_half.top()));
                    max_half.pop();
                }
                min_half.push(max_half.top()); 
                max_half.pop();
            }
            while(del_min.find(min_half.top())!=del_min.end()){
                del_min.erase(del_min.find(min_half.top()));
                min_half.pop();
            }
            while(del_max.find(max_half.top())!=del_max.end()){
                del_max.erase(del_max.find(max_half.top()));
                max_half.pop();
            }
            if(k&1) ans.push_back(max_half.top());
            else ans.push_back((1LL*min_half.top()+1LL*max_half.top())*1.0/2);
        }
        return ans;
    }
};
