class Solution {
    // For window of size k, to find median, we need to maintain a maxHeap and
    // a minHeap.

    // If we sort the elements of the window, maxHeap will store the lower half 
    // of the elements in the window. And, minHeap will store the upper half of 
    // the elements in the window.

    // We don't actually need to sort these elements, heaps will take care of it
    // We just need to make sure that:
    // for maxHeap, incomming element <= maxHeap.top() and 
    // for minHeap, incomming element >= minHeap.top()

    // We could then just look at the top of the heaps and figure out the median

    // At each step, as the window moves forward, we will remove the outgoing 
    // element from these heaps and add the incomming elements in the heaps.

    // The only trick here is that you need to avoid popping the element if
    // is is not at the top of the heap

private:
    priority_queue<int> maxHeap;
    priority_queue<int, vector<int>, greater<int>> minHeap;

public:
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        // Create a vector of double to store answer
        vector<double> ans;

        // Push first k elements to maxHeap
        for (int i = 0; i < k; ++i) {
            maxHeap.push(nums[i]);
        }

        // Balance the maxHeap and minHeap as they should have roughy the same 
        // number of elements
        while (maxHeap.size() > minHeap.size() + 1) {
            minHeap.push(maxHeap.top());
            maxHeap.pop();
        }

        // We will keep track of balance via this integer. Basically, 
        // Remove from max heap => balance++
        // Remove from min heap => balance--
        // Insert into maxHeap => balance--
        // Insert into minHeap => balance++
        // This way we can use the balance integer to maintain the sizes of 
        // both the heaps equal or off by one before calculating the median.
        // In between calculating medians, sizes can become off by 2. That is 
        // okay
        int balance = minHeap.size() - maxHeap.size();

        // There are 3 cases for calculating median
        // At this point, balance should be -1, 0, or 1
        // 1. If minHeap has more elements
        if (balance > 0) {
            // then median is the minHeap.top()
            ans.push_back((double)minHeap.top());
        // 2. If maxHeap has more elements
        } else if (balance < 0) {
            // then mendian is the maxHeap.top()
            ans.push_back((double)maxHeap.top());
        // 3. If both heaps have same number of elements, then median is average
        } else {
            // then median is the average
            ans.push_back(((double)maxHeap.top() + (double)minHeap.top()) / 2.0);
        }

        // We will not actully delete anything, but keep track of deleted ones
        // in this map. We will deal with them when they come at the top of the
        // respective heaps, by just ignoring them then.
        unordered_map<int, int> deleted;

        // For rest of the elements
        for (int i = k; i < nums.size(); ++i) {
            // Remove the outgoing element

            // If outgoing element is at top of maxHeap
            if (!maxHeap.empty() && maxHeap.top() == nums[i-k]) {
                // Remove it
                maxHeap.pop();
                // Update balance to indicate removal from maxHeap
                balance++;
            // Else if outgoing element is at top of minHeap
            } else if (!minHeap.empty() && minHeap.top() == nums[i-k]) {
                // Remove it
                minHeap.pop();
                // Update balance to indicate removal from minHeap
                balance--;
            // Else, it means that outgoing item is not at top of any heap
            // *** IMPORTANT: In this case we can remove the outgoing element.
            //                But, doing so would mean that we need to move
            //                some elements that are at top and move them back.
            //                This is inefficient.
            //                Instead, we will learn to ignore these elements 
            //                until they move to top of the heaps by recording
            //                them in the `deleted` map.
            } else {
                // If outgoing element is present in the maxHeap
                if (!maxHeap.empty() && maxHeap.top() > nums[i-k]) {
                    // Create an entry in the `deleted` map or increment count 
                    // if it already exists
                    deleted[nums[i-k]]++;
                    // Update balance to indicate that element is removed from 
                    // maxHeap
                    balance++;
                // Else if outgoing element is present in the maxHeap
                } else if (!minHeap.empty() && minHeap.top() < nums[i-k]) {
                    // Create an entry in the `deleted` map or increment count 
                    // if it already exists
                    deleted[nums[i-k]]++;
                    // Update balance to indicate that element is removed from 
                    // minHeap
                    balance--;
                }
            }

            // Insert incomming element

            // We need to insert in such a way that balance after insertion 
            // goes closer to 0 i.e both the heaps have roughly equal number of 
            // elements.

            // We also need to make sure that (as said earlier):
            // for maxHeap, incomming element <= maxHeap.top() and 
            // for minHeap, incomming element >= minHeap.top()

            // If minHeap has less elements
            if (balance < 0) {
                // If incomming element can go in minHeap
                if (nums[i] >= maxHeap.top()) {
                    // Insert incomming element into minHeap
                    minHeap.push(nums[i]);
                // Else if incomming element cannot go in minHeap
                } else {
                    // Move one element from maxHeap to minHeap
                    minHeap.push(maxHeap.top());
                    maxHeap.pop();
                    // Insert incomming element into the maxHeap
                    maxHeap.push(nums[i]);
                }
                // Update balance to indicate insertion in minHeap
                balance++;
            // Else if maxHeap has less elements
            } else if (balance > 0) {
                // If incomming element can go in maxHeap
                if (nums[i] <= minHeap.top()) {
                    // Insert incomming element into maxHeap
                    maxHeap.push(nums[i]);
                // Else if incomming element cannot go in maxHeap
                } else{
                    // Move one element from minHeap to maxHeap
                    maxHeap.push(minHeap.top());
                    minHeap.pop();
                    // Insert incomming element into the minHeap
                    minHeap.push(nums[i]);
                }
                // Update balance to indicate insertion in maxHeap
                balance--;
            // Else, it means that both the heaps have equal number of elements
            // In this case, we choose to make maxHeap longer than one element
            // if possible
            } else {
                // If incomming element can go in maxHeap
                if (nums[i] <= maxHeap.top()) {
                    // Insert incomming element into the maxHeap
                    maxHeap.push(nums[i]);
                    // Update balance to indicate insertion in maxHeap
                    balance--;
                // else incomming element cannot go in maxHeap
                } else {
                    // Insert incomming element into the minHeap
                    minHeap.push(nums[i]);
                    // Update balance to indicate insertion in minHeap
                    balance++;
                }
            }

            // To find median, we need to look at the top of the heaps.
            // So, ignore any elements that have already been deleted earlier
            // but not removed yet from the heap as they were not at the top

            // If top element at maxHeap was deleted
            while (!maxHeap.empty() && deleted[maxHeap.top()] > 0) {
                // Decrement the deleted counter
                deleted[maxHeap.top()]--;
                // Remove the element from the top of maxHeap
                maxHeap.pop();
            }
            // If top element at minHeap was deleted
            while (!minHeap.empty() && deleted[minHeap.top()] > 0) {
                // Decrement the deleted counter
                deleted[minHeap.top()]--;
                // Remove the element from the top of minHeap
                minHeap.pop();
            }

            // There are 3 cases for calculating median
            // At this point, balance should be -1, 0, or 1
            // 1. If minHeap has more elements
            if (balance > 0) {
                // then median is the minHeap.top()
                ans.push_back((double)minHeap.top());
            // 2. If maxHeap has more elements
            } else if (balance < 0) {
                // then mendian is the maxHeap.top()
                ans.push_back((double)maxHeap.top());
            // 3. If both heaps have same number of elements, then median is average
            } else {
                // then median is the average
                ans.push_back(((double)maxHeap.top() + (double)minHeap.top()) / 2.0);
            }
        }

        return ans;
    }
};
```

