class Solution {    //extension of -> 295. Find Median from Data Stream (refer that solution before solving this)

    PriorityQueue<Integer> min;                             //minHeap
    PriorityQueue<Integer> max;                             //maxHeap

    public double[] medianSlidingWindow(int[] nums, int k) {    //given fn.

        double[] ans = new double[nums.length - k + 1];     //answer array -> size is observation based

        min = new PriorityQueue<>();
        max = new PriorityQueue<>(Collections.reverseOrder());

        for(int i=0; i<nums.length; i++) {
            addNum(nums[i]);

            if(i - k + 1 >= 0) {
                // System.out.print(findMedian());
                ans[i - k + 1] = findMedian();
                remove(nums[i - k + 1]);                    //remove first element of window that got out of k size
            }
        }
        return ans;
    }

    public void remove(int val) {                           //remove the val from either heap
        if(val <= max.peek())
            max.remove(val);
        else if(val >= min.peek())
            min.remove(val);

        balanceHeap();                                      //self made fn. to balance it after each deletion
    }

    //taken from Q295. from here ↓
    public void addNum(int num) {
        if(max.isEmpty() || num <= max.peek()) {            //if num <= maxHeap's top element
            max.add(num);
        } else {
            min.add(num);
        }
        balanceHeap();                                      //self made fn. to balance it after each addition
    }

    public double findMedian() {
        if(max.size() == min.size()) {                      //even
            return ((double)max.peek() + min.peek()) / 2;
        } else {                                            //odd -> max.size() = min.size() + 1
            return max.peek();
        }
    }

    public void balanceHeap() {                             //self made fn. -> if it fails either 1) or 2) condition -> balance it
        if(max.size() > min.size() + 1) {                   //1) max = min + 1
            min.add(max.remove());
        } else if(min.size() > max.size()) {                //2) max = min
            max.add(min.remove());
        }
    }
}

