// using same idea as of median of data stream but using set for deletion operation
class Solution {
public:
    set<pair<double,int>> minHeap; // using as min heap
    set<pair<double,int>, greater<> >maxHeap; // using as max heap

    // to balance the sets after inserting a element
    void balance(){
        auto it = *minHeap.begin();
        minHeap.erase(minHeap.begin());
        maxHeap.insert(it);
        if(maxHeap.size() > minHeap.size()){
            it = *maxHeap.begin();
            minHeap.insert(it);
            maxHeap.erase(maxHeap.begin());
        }
    }

    // adjust the sets after removing the (i - k)th element from one of the sets 
    void adjust(){
        // sizes of heaps are initially same but element is deleted from minHeap 
        if(maxHeap.size() > minHeap.size()){
            auto it = *maxHeap.begin();
            minHeap.insert(it);
            maxHeap.erase(maxHeap.begin());
        }
        // size of minHeap was 1 more than maxHeap and element deleted from maxHeap
        if(minHeap.size() > 1 + maxHeap.size()){
            auto it = *minHeap.begin();
            minHeap.erase(minHeap.begin());
            maxHeap.insert(it);
        }
    }
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        vector<double> ans;
        int n = nums.size();
        //processing first k element
        for(int i = 0; i < k; i++){
            minHeap.insert({nums[i], i});
            balance(); // balancing after insertion
        }
        ans.push_back(minHeap.begin() -> first);
        if(k % 2 == 0){
            ans.back() = (ans.back() + maxHeap.begin()->first) / 2.0;
        }
        for(int i = k; i < n ; i++){
            //checking if (i-k)th element is in minHeap
            if(minHeap.count({nums[i-k], i-k})){
                minHeap.erase({nums[i-k], i-k});
            }else{
                maxHeap.erase({nums[i-k], i-k});
            }
            // after deletion operation adjusting the sets/heaps  
            adjust();
            // inserting i-th element and then balancing
            minHeap.insert({nums[i], i});
            balance();

            ans.push_back(minHeap.begin() -> first);
            if(k % 2 == 0){
                ans.back() = (ans.back() + maxHeap.begin()->first) / 2.0;
            }
        }
        return ans;
    }
};
