var medianSlidingWindow = function(nums, k) {

    let output = [];

    let left = 0;

    let right = 0;


   while(right < nums.length){



        if(right - left + 1 === k){

            let sortedSlice = nums.slice(left, right + 1).sort((a,b) => a-b);
            let middle = Math.floor((right + 1 - left) / 2);

            if(k % 2 === 0){
                output.push((sortedSlice[middle -1] + sortedSlice[middle]) / 2);
            } else {
                output.push(sortedSlice[middle]);
            }

            left++;
        }

        right++;
    }

    return output;
};

