class Solution {
    public double[] medianSlidingWindow(int[] nums, int k) {
        Comparator<Integer> comparator = (a, b) -> nums[a] != nums[b] ? Integer.compare(nums[a], nums[b]) : a - b;
        TreeSet<Integer> left = new TreeSet<>(comparator);
        TreeSet<Integer> right = new TreeSet<>(comparator);
        List<Double> list = new ArrayList<>();
        for (int i = 0; i < k; i++) {
            right.add(i);
        }
        for (int i = 0; i < k / 2; i++) {
            left.add(right.pollFirst());
        }
        putMedian(nums, list, left, right);
        for (int i = 0, j = k; j < nums.length; i++, j++) {
            insert(nums, left, right, j);
            remove(nums, left, right, i);
            rebalance(left, right);
            putMedian(nums, list, left, right);
        }
        double[] ans = new double[list.size()];
        for (int i = 0; i < list.size(); i++) {
            ans[i] = list.get(i);
        }
        return ans;
    }

    private void rebalance(TreeSet<Integer> left, TreeSet<Integer> right) {
        if (left.size() + 2 == right.size()) {
            left.add(right.pollFirst());
        } else if (left.size() == right.size() + 1) {
            right.add(left.pollLast());
        }
    }

    private void insert(int[] nums, TreeSet<Integer> left, TreeSet<Integer> right, int i) {
        if (left.size() != right.size()) {
            right.add(i);
            left.add(right.pollFirst());
        } else {
            left.add(i);
            right.add(left.pollLast());
        }
    }

    private void remove(int[] nums, TreeSet<Integer> left, TreeSet<Integer> right, int i) {
        if (!left.remove(i)) {
            right.remove(i);
        }
    }

    private void putMedian(int[] nums, List<Double> list, TreeSet<Integer> left, TreeSet<Integer> right) {
        if (left.size() != right.size()) {
            list.add(nums[right.first()] * 1.0);
        } else {
            long sum = (long)nums[left.last()] + (long)nums[right.first()];
            list.add((sum) / 2.0);
        }
    }
}
