class Solution {

    PriorityQueue<Integer> minHeap = new PriorityQueue<>();
    PriorityQueue<Integer> maxHeap = new PriorityQueue<>(Collections.reverseOrder());

    public double[] medianSlidingWindow(int[] nums, int k) {

        int nums_l = nums.length;
        double[] result = new double[nums_l - k + 1];

        if(k == 1) {
            for(int i = 0; i < nums_l; i++) {
                result[i] = nums[i];
            }

            return result;
        }

        if(k == 2) {
            for(int i = 1; i < nums_l; i++) {
                result[i - 1] = nums[i] / 2.0 + nums[i - 1] / 2.0;
            }

            return result;
        }

        for(int i = 0; i < nums_l; i++) {
            if(maxHeap.size() == 0 || maxHeap.peek() >= nums[i]) {
                maxHeap.add(nums[i]);
            } else {
                minHeap.add(nums[i]);
            }

            rebalanceHeaps();

            if(i >= k - 1) {
                result[i - k + 1] = findMeadian();
                removeElement(nums[i - k + 1]);
            }
        }

        return result;
    }

    private void removeElement(int num) {

        if(num <= maxHeap.peek()) {
            maxHeap.remove(num);
        } else {
            minHeap.remove(num);
        }

        rebalanceHeaps();
    }

    private double findMeadian() {

        if(minHeap.size() == maxHeap.size()) {
            return (minHeap.peek() / 2.0 + maxHeap.peek() / 2.0);
        }

        return maxHeap.peek();
    }

    private void rebalanceHeaps() {

        if(maxHeap.size() > minHeap.size() + 1) {
            minHeap.add(maxHeap.poll());
        } else if(maxHeap.size() < minHeap.size()) {
            maxHeap.add(minHeap.poll());
        }
    }
}
