#define REP(i, n) for(int i = 0; i < n; i++)
#define REPP(i, n) for(int i = 1; i <= n; i++)
#define ALL(obj) (obj).begin(), (obj).end()

class Solution {
public:
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        int n = nums.size();
        multiset<long> low, high;
        vector<double> ans;
        REP(i, n){
            if(i >= k){
                if(nums[i - k] <= *low.rbegin()) low.erase(low.find(nums[i - k]));
                else high.erase(high.find(nums[i - k]));
            }
            if(low.empty() || (!low.empty() && nums[i] <= *low.rbegin())) low.insert(nums[i]);
            else high.insert(nums[i]);

            while(!low.empty() && !high.empty() && *low.rbegin() > *high.begin()){
                high.insert(*low.rbegin());
                low.erase(prev(low.end()));
                low.insert(*high.begin());
                high.erase(high.begin());
            }
            while(!high.empty() && low.size() < high.size()){
                low.insert(*high.begin());
                high.erase(high.begin());
            }
            while(!low.empty() && low.size() > high.size() + 1){
                high.insert(*low.rbegin());
                low.erase(prev(low.end()));
            }

            if(i >= k - 1){
                if(k % 2 == 0) ans.push_back((*low.rbegin() + *high.begin()) / 2.0);
                else ans.push_back(*low.rbegin());
            }
        }
        return ans;   
    }
};
