#define REP(i, n) for(int i = 0; i < n; i++)
#define REPP(i, n) for(int i = 1; i <= n; i++)
#define ALL(obj) (obj).begin(), (obj).end()

class Solution {
private:
    priority_queue<long> low;
    priority_queue<long, vector<long>, greater<long>> high;
    unordered_map<long, int> removal_cnt;
    int balance;
public:
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {

        int n = nums.size();
        if(k == 1) return vector<double>(ALL(nums));
        balance = 0;
        vector<double> ans;
        REP(i, k){
            low.push(nums[i]);
            balance++;
        }
        while(!low.empty() && balance > 1){
            high.push(low.top());
            low.pop();
            balance -= 2;
        }
        //balance = 0, 1
        if(k % 2 == 0) ans.push_back((low.top() + high.top()) / 2.0);
        else ans.push_back(low.top());

        for(int i = k; i < n; i++){
            removal_cnt[nums[i - k]]++;
            if(!low.empty() && low.top() >= nums[i - k]) balance--;
            else balance++;
            if(low.empty() || (!low.empty() && low.top() >= nums[i])){
                low.push(nums[i]);
                balance++;
            }else{
                high.push(nums[i]);
                balance--;
            }
            remove_invalid_elements();
            balancing();
            remove_invalid_elements();
            if(k % 2 == 0) ans.push_back((low.top() + high.top()) / 2.0);
            else ans.push_back(low.top());
        }
        return ans;
    }

    void remove_invalid_elements(){
        while(!low.empty() && removal_cnt[low.top()] > 0){
            removal_cnt[low.top()]--;
            low.pop();
        }
        while(!high.empty() && removal_cnt[high.top()] > 0){
            removal_cnt[high.top()]--;
            high.pop();
        }
        return;
    }

    void balancing(){
        while(!high.empty() && balance < 0){
            if(removal_cnt[high.top()] > 0){
                removal_cnt[high.top()]--;
                high.pop();
                continue;
            }
            low.push(high.top());
            high.pop();
            balance += 2;
        }
        while(!low.empty() && balance > 1){
            if(removal_cnt[low.top()] > 0){
                removal_cnt[low.top()]--;
                low.pop();
                continue;
            }
            high.push(low.top());
            low.pop();
            balance -= 2;
        }
        return;
    }
};
