vector<double> medianSlidingWindow(vector<int>& nums, int k) {
    set<pair<int,int>> big;
    set<pair<int,int>, greater<pair<int,int>>> small;

    auto insert = [&](pair<int,int> a) {
        if (small.size() > big.size()) {
            small.insert(a);
            big.insert(*small.begin());
            small.erase(small.begin());
        } else {
            big.insert(a);
            small.insert(*big.begin());
            big.erase(big.begin());
        }
    };
    auto getmedian = [&]() {
        return (k % 2) ? small.begin()->first : 
        (0.0 + small.begin()->first + big.begin()->first) / 2;
    };

    vector<double> res;

    for (int i = 0; i < k; i++) {
        insert({nums[i], i});
    }
    res.push_back(getmedian());

    for (int i = 0; i+k < nums.size(); i++) {
        small.erase({nums[i], i});
        big.erase({nums[i], i});

        insert({nums[i+k], i+k});
        res.push_back(getmedian());
    }
    return res;
}

