class Solution:
    def medianSlidingWindow(self, nums: List[int], k: int) -> List[float]:
        if k == 1: return nums
        d,m = divmod(k,2)
        r,h1,h2,e2 = [],[],[],set()
		#We split the first k entries evenly.
		#We chose the upper heap to be somewhat larger if k is odd - but the solution easily can be modified for the opposite case (lower heap a bit larger)
        for i,v in enumerate(nums[:k]): 
            heapq.heappush(h2,(v,i))
            e2.add(i) #this is the list of the entries contained in h2. If an entry is not in e2 it must be in h1.
        for i in range(d):
            curr = heapq.heappop(h2)
            heapq.heappush(h1,(-curr[0], curr[1]))
            e2.remove(curr[1])
        if m: r.append(h2[0][0])
        else: r.append((-h1[0][0] + h2[0][0])/2)
		#For the rest of the code we need to make sure that the number of in-window entries in each heap stays constant
        for i,v in enumerate(nums[k:]):
            if i in e2: 
			    #The new out-of-window entry is in h2. 
				#Thus, to keep the number of in-windows entries in h2 constant we need to put the new in-window entry in h2
				#However, in order to make sure the monotonity is preserved we need to first put the new entry in h1 and then pop the top of h1 into h2
                curr = heapq.heappushpop(h1, (-v,i+k))
                heapq.heappush(h2,(-curr[0], curr[1]))
                e2.add(curr[1])
            else:
			    #The new out-of-window entry is in h1. 
				#Therefore, in order to keep the number of in-window entries in h1 constant we need to add the new in-window entry to h1.
				#However, in order to make sure the monotonity is preserved we need to first put the new entry in h2 and then pop the top of h2 into h1
                curr = heapq.heappushpop(h2,(v, i+k))
                e2.add(i+k)
                e2.remove(curr[1])
                heapq.heappush(h1, (-curr[0], curr[1]))
			#We need to make sure that both tops are not out-of-window:
            while h1 and h1[0][1] <= i: heapq.heappop(h1)
            while h2 and h2[0][1] <= i: heapq.heappop(h2)
            if m: r.append(h2[0][0])
            else: r.append((-h1[0][0] + h2[0][0])/2)
        return r
