class Solution {
    public double[] medianSlidingWindow(int[] nums, int k) {
        int n = nums.length;
        double []window = new double[n - k + 1];
        PriorityQueue<Long>low = new PriorityQueue<>(Collections.reverseOrder());
        PriorityQueue<Long>high = new PriorityQueue<>();
        for(int i=0;i<k;i++){
            low.add((long)nums[i]);
            high.add(low.remove());
            if(high.size() > low.size()){
                low.add(high.remove());
            }
        }
        int counter = 0;
        if(low.size() == high.size()){
            window[counter++] = (low.peek() + high.peek())/2.0;
        }
        else{
            window[counter++] = low.peek() * 1.0;
        }
        for(int i=k;i<n;i++){
            long start = (long)nums[i - k];
            if(!low.remove(start)){
                high.remove(start);
            }
            low.add((long)nums[i]);
            high.add(low.remove());
            if(high.size() > low.size()){
                low.add(high.remove());
            }
            if(low.size() == high.size()){
                window[counter++] = (low.peek() + high.peek())/2.0;
            }
            else window[counter++] = low.peek()*1.0;

        }
        return window;
    }
}
