//        sliding window using binary search for element removal and 
//        using insertion sort for element addition

    double compute_median(vector<int> &window){
        if(window.size() & 1){
           // odd num of elements
           return window[(window.size()/2) ]; 
        }else{
           // even number of elements 
           return (1.0)*(1L*window[(window.size()/2) ] + 1L*window[(window.size()/2) -1])/2.0;
        }
    }    
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        vector<double> result;

        // prepare the initial window
        int s=0;
        int e=k-1;
        vector<int> window=vector(nums.begin()+s,nums.begin()+e+1);

        //sort the initial window
        sort(window.begin(),window.end());

        while(e<nums.size()){
            result.push_back(compute_median(window));

            // delete the element nums[s] from window  O(T)- O(log(k))
            // using binary search
            window.erase(lower_bound(window.begin(), window.end(), nums[s]));
            s++;
            e++;
            if(e<nums.size()){
                // binary search location to insert and insert element nums[e]
                // O(T) = O(k) however emperically it should perform better 
                // 
                window.insert(lower_bound(window.begin(), window.end(), nums[e]),nums[e]);
            }
        }
        return result;
    }

