class Solution {
    public double[] medianSlidingWindow(int[] nums, int k) {

        int l = 0;
        int r = l+k;
        double[] res = new double[nums.length-k+1 ];
        int index= 0;
        PriorityQueue<Integer> maxHeap = new PriorityQueue<>(Collections.reverseOrder());
        PriorityQueue<Integer> minHeap = new PriorityQueue<>();
        for(int i = l; i<k; i++){
            addNum(nums[i], maxHeap, minHeap);
        }

        while(r<=nums.length && index < res.length){  

            double medium = findMedium( maxHeap, minHeap);
            res[index++] = medium;

            //start sliding window 
            if(r<nums.length){
                removeNum(nums[l], maxHeap, minHeap);
                addNum(nums[r], maxHeap, minHeap);
                l+=1;
                r+=1;
            }
        }
        return res;
    }

    private void addNum(int num, PriorityQueue<Integer> maxHeap, PriorityQueue<Integer> minHeap){
          if(maxHeap.isEmpty() || num < maxHeap.peek()){
              maxHeap.add(num);
          }
          else{
              minHeap.add(num);
          }

          maintainBalance(maxHeap, minHeap);

    }
    private void removeNum(int num,PriorityQueue<Integer> maxHeap, PriorityQueue<Integer> minHeap){
         if(num <= maxHeap.peek()){
             maxHeap.remove(num);
             maintainBalance(maxHeap, minHeap);
         }else{
             minHeap.remove(num);
             maintainBalance(maxHeap, minHeap);
        }
    }
    private double findMedium(PriorityQueue<Integer> maxHeap, PriorityQueue<Integer> minHeap){
        if(maxHeap.size() > minHeap.size())
            return maxHeap.peek() * 1.0;
        if(maxHeap.size() < minHeap.size()){
            return minHeap.peek() * 1.0;
        }
        else{
            long num = (long)maxHeap.peek() + (long)minHeap.peek();
            return num/2.0;
        }
    }

    private void maintainBalance(PriorityQueue<Integer> maxHeap, PriorityQueue<Integer> minHeap){
          if(!maxHeap.isEmpty() && !minHeap.isEmpty() && maxHeap.peek() > minHeap.peek()){
              int val = maxHeap.poll();
              minHeap.add(val);
          }
          if(maxHeap.size() - minHeap.size() > 1){
              int val = maxHeap.poll();
              minHeap.add(val);
          }
          if(minHeap.size() - maxHeap.size() > 1 ){
              int val = minHeap.poll();
              maxHeap.add(val);
          }
    }
}

