class Solution {
public:
    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        int n = nums.size();
        vector<double> ans; // (n-k+1);
        multiset<int> hash;
        bool odd = k&1;
        int i;
        for(i = 0; i<k; i++) hash.insert(nums[i]);
        auto p = hash.begin();
        for(int j=0; j<k/2; j++) p++;
        for(; i<=n; i++) 
        {
            if (odd) 
            {
                ans.push_back(*p);
            }
            else
            {
                auto m1 = p;
                m1--;
                ans.push_back(((double)*m1+*p)/2);
            }
            if(i==n) break;

            hash.insert(nums[i]);
            if(nums[i] < *p) p--;
            if(nums[i-k]<= *p) p++;
            hash.erase(hash.lower_bound(nums[i-k])); 
        }
        return ans;

    }
};
