class Solution {
public:
    multiset<long long> first;
    multiset<long long> sec;

    vector<double> medianSlidingWindow(vector<int>& a, int k) {
        vector<double> ans;
        long long n = a.size();
        long long start = 0;

        for(int i=0; i<n; i++){
            long long x = a[start];
            if(i>=k){
                //delete operation
                if(first.find(x)!=first.end()){
                    first.erase(first.find(x));
                    if(first.size()<sec.size()){
                        first.insert(*sec.begin());
                        sec.erase(sec.begin());
                    }
                }
                else{
                    sec.erase(sec.find(x));
                    if(first.size()>sec.size()+1){
                        sec.insert(*first.rbegin());
                        auto it = first.end(); it--;
                        first.erase(it);
                    }
                }
                start++;
            }
            //insert operation
            x = a[i];
            if(first.size()==sec.size()){
                if(first.size()==0 or x<=*first.rbegin()){
                    first.insert(x);
                }else{
                    sec.insert(x);
                    first.insert(*sec.begin());
                    sec.erase(sec.begin());
                }
            }else{
                if(x<*first.rbegin()){
                    sec.insert(*first.rbegin());
                    auto it = first.end(); it--;
                    first.erase(it);
                    first.insert(x);
                }else{
                    sec.insert(x);
                }
            }
            if(i>=k-1)
            k%2==0 ? ans.push_back(double(*first.rbegin()+ *sec.begin())/2) : ans.push_back(double(*first.rbegin()));
        }

        return ans;
    }
};
