class Solution {
public:
    multiset<double> pqmin;
    multiset<double, greater<double>> pqmax;


    void addNum(int num) {
       if(pqmin.size() == pqmax.size()){
            if(pqmin.size() == 0){
                pqmin.insert(num);
                return;
            }
            if(num > *pqmax.begin()){
                pqmin.insert(num);
                return;
            }
            pqmax.insert(num);
        }
        else{
            if(pqmin.size() > pqmax.size()){
                if(num > *pqmin.begin()){
                    double temp = *pqmin.begin();
                    pqmin.erase(pqmin.lower_bound(*pqmin.begin()));
                    pqmax.insert(temp);
                    pqmin.insert(num);
                  return;  
                }
                pqmax.insert(num);

            }else{
                if(num < *pqmax.begin()){
                    double temp = *pqmax.begin();
                    pqmax.erase(pqmax.lower_bound(*pqmax.begin()));
                    pqmin.insert(temp);
                    pqmax.insert(num);
                  return;  
                }
                pqmin.insert(num);
            }
        }
    }

   double findMedian() {
        if(pqmin.size() == pqmax.size()){
            return (*pqmin.begin() + *pqmax.begin())/ 2.0;
        }else if (pqmin.size() > pqmax.size())
            return *pqmin.begin();
        else
            return *pqmax.begin();
    }

    void erase(int num){
        if(*pqmin.begin()>num){
            pqmax.erase(pqmax.lower_bound(num));
        }else{
            pqmin.erase(pqmin.lower_bound(num));
        }
    }

    vector<double> medianSlidingWindow(vector<int>& nums, int k) {
        vector<double> op;
        for(int i = 0; i<k; i++){
            addNum(nums[i]);
        }        
        op.push_back(findMedian());
        for(int i = k; i<nums.size(); i++){
            erase(nums[i - k]);
            addNum(nums[i]);
            op.push_back(findMedian());
        }
        return op;
    }
};

