    int numRabbits(vector<int>& answers) {
        map<int,int>mp;
        for(auto i : answers) mp[i]++;
        int ans = 0;
        for(auto i : mp){
            int x = i.first;
            int y = i.second;
            ans += ( (x+1) * (y/(x+1)) );
            if(y%(x+1)) ans += (x+1);
        }
        return ans;
    }
};
