class Solution {
public:
    int numRabbits(vector<int>& answers) {
        unordered_map<int, int> mpp;
		// Get the count of i + 1
        for(auto i : answers) mpp[i+1]++;
        int ans = 0;
        for(auto i : mpp){
            int t1 = i.first;
            int t2 = i.second;
            int res = t2 / t1;
			// using Ceil function
            if(t2 % t1 != 0) res++;
            ans += (res * t1);
        }
        return ans;
    }
};
