int numRabbits(vector<int>& answers) {

    int maxi = -1;

    for(int i=0; i<answers.size(); i++){
        maxi = max(maxi, answers[i]);
    }

    vector<int> store(maxi+1, 0);

    for(int i=0; i<answers.size(); i++){
        store[answers[i]]++;
    }

    int ans = store[0];

    for(int i=1; i<store.size(); i++){

        // When few rabits say that there are lots of rabits like them
        if(store[i] != 0 && store[i] <= i){
            ans += i + 1;
        }

        // When lots of rabits say that there are few rabits like them
        else if(store[i] > i){
            ans += store[i];

            int remaining = store[i] % (i+1);

            if(remaining != 0){
                int need = i + 1 - remaining;
                ans += need;
            }

        }
    }

    return ans;
}

