class Solution {
public:
    int numRabbits(vector<int>& answers) {

        unordered_map<int,int> map;

        for(int num : answers ){
            map[num]++;
        }

        int ans = 0;

        for(auto itr : map){

            if(itr.first == 0){
                ans += itr.second;
            }

            else if(itr.second <= itr.first + 1){
                ans += itr.first + 1;
            }

            else{
                int count = itr.second / (itr.first + 1);

                if(itr.second % (itr.first+1) != 0)
                    count++;

                ans += (itr.first + 1) * count;
            }
        }

        return ans;
    }
};
