int numRabbits(vector<int>& answers) {
        int ans = 0;
        map<int, int> mymap;

        for(int x: answers) {
            int key = x;
            if(mymap.find(key) != mymap.end()) {
                int currentVal = mymap[key];
                int maxVal = key + 1;

                if(currentVal < maxVal) {
                    mymap[key] += 1;
                }
                else {
                    ans += maxVal;
                    mymap[key] = 1;
                }
            }
            else {
                mymap[key] = 1;
            }
        }

        for(auto &mapElement: mymap) {
            ans += (mapElement.first+1);
        }

        return ans;
    }
