class Solution:
    def numRabbits(self, answers: List[int]) -> int:
        res = 0
        seen = {}
        for answer in answers:
            if answer == 0:
                res += 1
            elif answer not in seen:
                res += answer+1
                seen[answer] = 1
            else:
                seen[answer] += 1
				#seen[answer] = answer+1 means we have counted all rabbits with this same color, if we see another answer it could be another color
                if seen[answer] == answer+1:
                    del seen[answer]
        return res
