class Solution {
    public int numRabbits(int[] answers) {
        Map<Integer,Integer> differentColorGroups = new HashMap<>();

        int rabbitCount = 0;

        for(int answer : answers){ // I'am rabbit (I am telling that how many rabbits are having same color as me)
            if(answer == 0){ // I'am distinct, I dont belong to any group
                rabbitCount += 1;
            }
            else if(differentColorGroups.get(answer) == null){ // There is no group with this color, so create one
                rabbitCount += answer+1; // (there are answer+1(including me) rabbits having same colors) - a group size
                differentColorGroups.put(answer, answer); // now in future this group can accomodate 'answer' number of rabbits
            }
            else{
                int count = differentColorGroups.get(answer); //get the count of the group size
                if(count == 1){ //this group can accomodate 1 more rabbit, I am getting into this group so now remove this group for future use
                    differentColorGroups.remove(answer); //remove this group, because it cant accomodate new member
                }
                else{//I belong to this group, now this group can accomodate (Count-1) rabbit for future
                    differentColorGroups.put(answer, count-1);
                }
            }
        }

        return rabbitCount;
    }
}
