class Solution {
public:
    int helper(vector<int>&answers){
        priority_queue<int>pq(answers.begin(),answers.end());

        int rabbitCount = 0, num , k;

        while(!pq.empty()){ 
            num = k = pq.top();

			//There will be k + 1 rabbit of same color if answer count is k so we add k+1 to rabbitCount
            rabbitCount += k + 1;
            pq.pop();

			// We have to skip k rabbit of same color 
            while(k-- > 0 && !pq.empty() && pq.top() == num){
                pq.pop();
            }
        }

        return rabbitCount;
    }
    int numRabbits(vector<int>& answers) {
        return helper(answers);
    }
};
```

