class Solution {
    public int numRabbits(int[] ans) {
        if(ans == null || ans.length == 0)
            return 0;

        int count = 0;
        Map<Integer, Integer> map = new HashMap<>();
        for(int a : ans) {
            if(a == 0) {
                // There is no other rabbit of the same color, this is a unique rabbit
                count++;
                continue;
            }
            if(map.containsKey(a)) {
                // Some other rabbit has previously mentioned the same grouping number (same color)
                int expected = map.get(a);
                expected--;
                if(expected == 0) // Since we have found all the rabbits with the same grouping, lets reset
                    map.remove(a);
                else
                    map.put(a, expected);
            } else {
                // No other rabbit of the same color has been found
                map.put(a, a);
            }
            count++;
        }

        // Account for rabbits in the forest
        for(Map.Entry<Integer, Integer> entry : map.entrySet())
            count += entry.getValue();

        return count;

    }
}
